mapper.insert:
insert into t_shopping_seller(seller_id, seller_pw, seller_name, seller_tel1, seller_tel2, seller_tel3, seller_hp1, seller_hp2, seller_hp3, seller_email, seller_zipcode, seller_roadAddr, seller_jibunAddr, seller_namujiAddr, seller_joinDate, seller_bank_num, seller_bank_name, seller_bank_com, seller_company, seller_company_info, seller_img1, seller_confirmDate
) values(#{seller_id}, #{seller_pw}, #{seller_name}, #{seller_tel1}, #{seller_tel2}, #{seller_tel3}, #{seller_hp1}, #{seller_hp2}, #{seller_hp3}, #{seller_email}, #{seller_zipcode}, #{seller_roadAddr}, #{seller_jibunAddr}, #{seller_namujiAddr}, now(), #{seller_bank_num}, #{seller_bank_name}, #{ seller_bank_com}, #{seller_company}, #{seller_company_info}, #{seller_img1}, #{seller_confirmDate}
)

