mapper.select:
	    SELECT gsq.*
        FROM T_SHOPPING_GOODSQNA gsq
        JOIN T_SHOPPING_GOODS gs ON gsq.goods_id = gs.goods_id
        WHERE gs.seller_id = #{seller_id} 
        LIMIT #{startIndex}, #{itemsPerPage};

